snippets.css:
blockquote.saberMais {
    background: #e3f0f0;
    border: none;
    background-color: #fff;
    border-width: 2px;
    border-style: solid;
    border-radius: 2em;
    border-color: #60b3ad;
}

blockquote {
    position: relative;
    clear: both;
    padding: 2em 1em 1em;
    margin: 5em 1em 1.4em 1em;
    quotes: none;
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: transparent;
    display: block;
    margin-block-start: 1em;
    margin-block-end: 1em;
    margin-inline-start: 40px;
    margin-inline-end: 40px;
    unicode-bidi: isolate;
}


blockquote .tituloQuote {
    font-family: 'Simply Rounded', Helvetica, Arial, sans-serif;
    position: absolute;
    top: -2em;
    color: #FFFFFF;
    line-height: 0;
    display: inline-flex;
    align-items: center;
    font-size: 1.3em;
    font-weight: normal;
    text-transform: uppercase;

}

blockquote .tituloQuote:before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
margin-top:20px;
    width: 60px;
    -webkit-border-top-left-radius: 2em;
    -webkit-border-bottom-left-radius: 2em;
    -moz-border-radius-topleft: 2em;
    -moz-border-radius-bottomleft: 2em;
    border-top-left-radius: 2em;
    border-bottom-left-radius: 2em;
}

*:before, *:after {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

blockquote span.inner {
    border-top-left-radius: 1em;
    border-bottom-left-radius: 1em;
    line-height: 2em;
    padding: 0 0 0 50px;
    background: #60b3ad;
}


blockquote span.inner {
    background: #60b3ad;
    background-image: initial;
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: rgb(96, 179, 173);
}

blockquote span.detalhe {
    border-color: #60b3ad transparent #60b3ad #60b3ad;
    display: inline-block;
    width: 0px;
    height: 0px;
    padding-right: 1em;
    border: 1em solid;
}

blockquote.saberMais > .tituloQuote:after {
    width: 100px;
    height: 100px;
    background-position: -500px 0;
    content: '';
    position: absolute;
    left: -40px;
    top: -20px;
    background: transparent url(../image/ef1-sprites.png) no-repeat;
}


span.detalhe {
    border-color: #60b3ad transparent #60b3ad #60b3ad;
display: inline-block;
    width: 0px;
    height: 0px;
    padding-right: 1em;
    border: 1em solid;
}

