/* pagina√ß√£o */
blockquote.pagina {
  border: 0;
  background-color: #fff;
  height: 75px;
  width: 65px;
  position: relative;
  right: 2px;
  margin-left: 10px !important;
  clear: both;
  z-index: 3;
  text-align: center;
}

blockquote.pagina:before,
blockquote.pagina:after {
  content: "";
  display: block;
  position: absolute;
  left: 0;
  right: 0;
}

blockquote.pagina:before {
  height: 37px;
  top: 70%;
  background:
    linear-gradient(-135deg, #df7527 17.5px, transparent 0) 0 17.5px,
    linear-gradient(135deg, #df7527 17.5px, transparent 0) 0 17.5px;
  background-position: top left;
  background-repeat: repeat-x;
  background-size: 25px 25px;
  z-index: 2;
}

blockquote.pagina:after {
  height: 45px;
  top: 65%;
  background:
    linear-gradient(-135deg, #fff 17.5px, transparent 0) 0 17.5px,
    linear-gradient(135deg, #fff 17.5px, transparent 0) 0 17.5px;
  background-position: top left;
  background-repeat: repeat-x;
  background-size: 25px 25px;
  z-index: 2;
}

/* n√∫mero da p√°gina */
blockquote.pagina .paginacao {
  color: #3d1133;
  font-family: 'Nunito';
  font-weight: 900;
  margin-top: 20px;
  text-indent: 0;
}

