h4.desenvolvendoHab {
  margin: 0 0 0.6em;
  padding: 0;
  font-family: 'dT Jakob';
  font-weight: bold;  
  color: #1e57a6; /* tom azul do texto */
  background: none;
}

.alternativaLetra {
  display: block;
  margin: 0.6em 0;
  font-family: inherit;
  line-height: 1.2;
}

/* esconde o input */
.alternativaLetra label > input {
  position: absolute;
  opacity: 0;
}

/* √°rea clic√°vel */
.alternativaLetra label {
  position: relative;
  display: inline-block;
  width: 44px;
  height: 44px;
  margin-right: 10px;
  vertical-align: middle;
  cursor: pointer;
}

/* c√≠rculo */
.alternativaLetra .check {
  position: relative;
  display: inline-block;
  width: 44px;
  height: 44px;
  border-radius: 50%;
  border: 2px solid #2b6cb0;
  box-sizing: border-box;
}

/* preenchimento */
.alternativaLetra .check::after {
  content: "";
  position: absolute;
  inset: 6px;
  border-radius: 50%;
  background: transparent;
}

.alternativaLetra label > input:checked + .check::after {
  background: #2b6cb0;
}

/* letra no centro */
.alternativaLetra .letra {
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  color: #2b6cb0;
  font-size: 0.95em;
}

.alternativaLetra label > input:checked + .check .letra {
  color: #fff;
}
